# Parsear el HTML con BeautifulSoup
soup = BeautifulSoup(html_element, 'html.parser')

# Encontrar todos los elementos <a> que contienen información de las tiendas Makro
tiendas_makro = soup.find_all('a', class_='StoreModal_localsItem__As38H')

# Iterar sobre las tiendas y extraer la información
informacion_tienda = []
for tienda in tiendas_makro:
    nombre = tienda.find('h3', class_='StoreModal_localsItemTitle__jYzhF').text
    direccion = tienda.find('p', class_='StoreModal_localsItemAddress__m6NDG').text
    ubicacion = tienda.find('p', class_='StoreModal_localsItemLocation___4Wt0').text

    informacion_tienda.append({
        'nombre': nombre,
        'direccion': direccion,
        'ubicacion': ubicacion
    })

# Imprimir la información de las tiendas
for tienda in informacion_tienda:
    print(f"Nombre: {tienda['nombre']}")
    print(f"Dirección: {tienda['direccion']}")
    print(f"Ubicación: {tienda['ubicacion']}")
